import React, { useState } from 'react';
import { Jumbotron } from 'reactstrap';


function cadastrar() {

  const [meta, setMeta] = useState({
    name: '',
    description: '',
    status: ''
  });

  const [response, setResponse] = useState({
    formSave: false,
    type: '',
    message: ''
  })

  const onChangeInput = e => setMeta({ ...meta, [e.target.name]: e.target.value });

  const sendMeta = async (e) => {
    e.preventDefault();
    setResponse({ formSave: true });

    try {
      const res = await fetch('http://localhost:8080/metas', {
        method: 'POST',
        body: JSON.stringify(meta),
        headers: { 'Accept': 'application/json', 'Content-Type': 'application/json' }
      });

      const responseEnv = await res.json();
      if (responseEnv.error) {
        setResponse({
          formSave: false,
          type: 'error',
          message: responseEnv.message
        });
      } else {
        setResponse({
          formSave: false,
          type: 'success',
          message: responseEnv.message
        });
      }
    } catch (error) {
      setResponse({
        formSave: false,
        type: 'error',
        message: 'Erro: meta nÃ£o cadastrada com sucesso'
      });
    }

  }

  return (
    <>
      <Jumbotron fluid className="form">
        <style>
          {`.form{
              background-color: #171941;
              color: #bf38ac;
              padding-top: 30px;
                        padding-bottom: 150px;
                        margin-bottom: 0rem !important;
          }`}
        </style>
        <h1>Cadastrar minha meta.</h1>
        <hr />
        {
          response.type === 'error' ? <p> {response.message}</p> : ""
        }
        {response.type === 'success' ? <p> {response.message}</p> : ""}

        <form onSubmit={sendMeta}>
          <label>Nome</label>
          <input
            type="text"
            name="name"
            id="name"
            placeholder="Nome da meta"
            onChange={onChangeInput}
          /> <br /> <br />

          <label>DescriÃ§Ã£o</label>
          <input
            type="text"
            name="description"
            id="description"
            placeholder="DescriÃ§Ã£o da meta"
            onChange={onChangeInput}
          /><br /> <br />

          <label>Status</label>
          <input
            type="text"
            name="status"
            id="status"
            placeholder="Status da meta"
            onChange={onChangeInput}
          /><br /> <br />

          {response.formSave ? <button type="submit" disabled>Enviando ... </button> : <button type="submit">Cadastrar</button>}
        </form>
      </Jumbotron>
    </>
  )
}


export default cadastrar
