import React from 'react';
import { Jumbotron, Container, ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText } from 'reactstrap';
import Menu from '../components/Menu';

function Home({ data }) {
  return (
    <>
      <Menu />
      <Jumbotron fluid className="list">
        <style>
          {`.list{
                background-color: #171941;
                padding-top: 30px;
                padding-bottom: 150px;
                margin-bottom: 0rem !important;
            }.title-top{
                color: #bf38ac;
            }.list-meta{
                background-color: #0d345d !important;
                border-color: #4a0242 !important;
                color: #fff;
            }`
          }
        </style>
        <Container>
          <h1 className="display-4 text-center title-top">Minhas metas.</h1>
          <hr />
          <ListGroup>
            {
              data.metas.map(meta => (

                <div key={meta._id}>
                  <ListGroupItem>
                  <h2>{meta.name}</h2>
                  <p>{meta.description}</p>
                  <p>{meta.status}</p>
                  <hr />
                  </ListGroupItem>
                </div>

              ))
            }
          </ListGroup>
        </Container>
      </Jumbotron>
    </>
  )
}

export async function getServerSideProps() {
  try {
    const response = await fetch(`http://localhost:8080/metas`);
    const data = await response.json();
    console.log(data);
    return { props: { data } }
  } catch (error) {
    return {
      notFound: true,
      error: `Dados não encontrados ${error}`
    }
  }

}
export default Home
